import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { serviceAPI, incidentAPI } from '../services/api';
import ServiceStatus from '../components/ServiceStatus';
import IncidentCard from '../components/IncidentCard';

const AdminDashboard = () => {
  const { user, logout } = useAuth();
  const [services, setServices] = useState([]);
  const [incidents, setIncidents] = useState([]);
  const [newServiceName, setNewServiceName] = useState('');
  const [newIncident, setNewIncident] = useState({
    title: '',
    description: '',
    serviceIds: []
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      const [servicesRes, incidentsRes] = await Promise.all([
        serviceAPI.getServices(),
        incidentAPI.getIncidents()
      ]);

      setServices(servicesRes.data);
      setIncidents(incidentsRes.data);
    } catch (error) {
      console.error('Error loading data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleStatusChange = async (serviceId, newStatus) => {
    try {
      await serviceAPI.updateServiceStatus(serviceId, newStatus);
      setServices(prev => prev.map(service => 
        service.id === serviceId ? { ...service, status: newStatus } : service
      ));
    } catch (error) {
      console.error('Error updating status:', error);
    }
  };

  const handleCreateService = async (e) => {
    e.preventDefault();
    if (!newServiceName.trim()) return;

    try {
      const response = await serviceAPI.createService(newServiceName);
      setServices(prev => [...prev, response.data]);
      setNewServiceName('');
    } catch (error) {
      console.error('Error creating service:', error);
    }
  };

  const handleCreateIncident = async (e) => {
    e.preventDefault();
    if (!newIncident.title.trim() || !newIncident.description.trim()) return;

    try {
      const response = await incidentAPI.createIncident(
        newIncident.title,
        newIncident.description,
        newIncident.serviceIds
      );
      setIncidents(prev => [response.data, ...prev]);
      setNewIncident({ title: '', description: '', serviceIds: [] });
    } catch (error) {
      console.error('Error creating incident:', error);
    }
  };

  const handleResolveIncident = async (incidentId) => {
    try {
      await incidentAPI.resolveIncident(incidentId);
      setIncidents(prev => prev.map(inc => 
        inc.id === incidentId ? { ...inc, status: 'resolved' } : inc
      ));
    } catch (error) {
      console.error('Error resolving incident:', error);
    }
  };

  if (loading) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <div>Loading...</div>
      </div>
    );
  }

  return (
    <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '20px' }}>
      <header style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: '40px',
        paddingBottom: '20px',
        borderBottom: '1px solid #e5e7eb'
      }}>
        <div>
          <h1 style={{ fontSize: '32px', fontWeight: '700', marginBottom: '4px' }}>
            Admin Dashboard
          </h1>
          <p style={{ color: '#6b7280' }}>Welcome, {user?.email}</p>
        </div>
        <button
          onClick={logout}
          style={{
            padding: '8px 16px',
            backgroundColor: '#ef4444',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Logout
        </button>
      </header>

      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '40px' }}>
        <section>
          <h2 style={{ fontSize: '24px', fontWeight: '600', marginBottom: '20px' }}>
            Services Management
          </h2>

          <form onSubmit={handleCreateService} style={{ marginBottom: '20px' }}>
            <div style={{ display: 'flex', gap: '8px' }}>
              <input
                type="text"
                value={newServiceName}
                onChange={(e) => setNewServiceName(e.target.value)}
                placeholder="Service name"
                style={{
                  flex: 1,
                  padding: '8px 12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '4px'
                }}
              />
              <button
                type="submit"
                style={{
                  padding: '8px 16px',
                  backgroundColor: '#10b981',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
              >
                Add Service
              </button>
            </div>
          </form>

          {services.map(service => (
            <ServiceStatus
              key={service.id}
              service={service}
              showControls={true}
              onStatusChange={handleStatusChange}
            />
          ))}
        </section>

        <section>
          <h2 style={{ fontSize: '24px', fontWeight: '600', marginBottom: '20px' }}>
            Incident Management
          </h2>

          <form onSubmit={handleCreateIncident} style={{ marginBottom: '20px' }}>
            <div style={{ marginBottom: '12px' }}>
              <input
                type="text"
                value={newIncident.title}
                onChange={(e) => setNewIncident(prev => ({ ...prev, title: e.target.value }))}
                placeholder="Incident title"
                style={{
                  width: '100%',
                  padding: '8px 12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '4px',
                  boxSizing: 'border-box'
                }}
              />
            </div>
            <div style={{ marginBottom: '12px' }}>
              <textarea
                value={newIncident.description}
                onChange={(e) => setNewIncident(prev => ({ ...prev, description: e.target.value }))}
                placeholder="Incident description"
                rows="3"
                style={{
                  width: '100%',
                  padding: '8px 12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '4px',
                  boxSizing: 'border-box',
                  resize: 'vertical'
                }}
              />
            </div>
            <button
              type="submit"
              style={{
                padding: '8px 16px',
                backgroundColor: '#ef4444',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              Create Incident
            </button>
          </form>

          {incidents.map(incident => (
            <div key={incident.id} style={{ position: 'relative' }}>
              <IncidentCard incident={incident} />
              {incident.status !== 'resolved' && (
                <button
                  onClick={() => handleResolveIncident(incident.id)}
                  style={{
                    position: 'absolute',
                    top: '16px',
                    right: '16px',
                    padding: '4px 8px',
                    backgroundColor: '#10b981',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    fontSize: '12px',
                    cursor: 'pointer'
                  }}
                >
                  Resolve
                </button>
              )}
            </div>
          ))}
        </section>
      </div>
    </div>
  );
};

export default AdminDashboard;
