import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

// Complete extension blocking
(function() {
  // Block MetaMask and other wallet extensions
  delete window.ethereum;
  delete window.web3;

  // Prevent extension script injection
  const observer = new MutationObserver(function(mutations) {
    mutations.forEach(function(mutation) {
      mutation.addedNodes.forEach(function(node) {
        if (node.tagName === 'SCRIPT' && node.src && node.src.includes('chrome-extension')) {
          node.remove();
        }
      });
    });
  });

  observer.observe(document.documentElement, {
    childList: true,
    subtree: true
  });

  // Override console to filter extension messages
  const methods = ['error', 'warn', 'log', 'info'];
  methods.forEach(method => {
    const original = console[method];
    console[method] = function(...args) {
      const message = args.join(' ');
      if (message.includes('MetaMask') || message.includes('chrome-extension') || message.includes('Failed to connect')) {
        return;
      }
      original.apply(console, args);
    };
  });
})();

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);
